'use strict';
$(function() {

    // ================================ 变量声明 ================================ //
    // ContextPath
    var sContextPath = _.CONTEXT_PATH;
    // 公司信息一览表格
    var oDataTable;
    
    $("#txtJE").get(0).focus();
    // ================================ 方法声明 ================================ //
    // JS插件渲染及画面初期事件绑定
    function initializePage() {
        var oDataTableOption = _.DATATABLES_OPTIONS;
        oDataTableOption.ajax = {
            url: _.resolvePath(sContextPath, 'api/organizations'),
            data: function(oData) {
                $.extend(oData, $('form').serializeObject());
            }
        };

        oDataTableOption.columns = [ 
            {
                width: '35%',
                data: 'tenpoNo'
            },
            {
                width: '35%',
                data: 'tenpoName'
            }
      ]

        oDataTableOption.columnDefs = [
            {
                createdCell: function(dTd, sData) {
                    $(dTd).attr('title', sData);
                },
                targets : [ 0, 1 ]
            },
            {
                createdCell: function(dTd, sData, oData) {
                    var nId = oData.tenpoSeqNo;
                    var sBtn = '&nbsp&nbsp<a href="javascript:;"' + 'data-click="add" data-click-data="'
                            + nId + '" title="进入"><i class="fa fa-arrow-right"></i></a>&nbsp&nbsp';
                  
                    $(dTd).html(sBtn);
                 },
                 targets : [ 2 ]
             }
         ]

        // 获取初始化表格
        oDataTable = $('#orgnaizationTbl').DataTable(oDataTableOption);

        //事件绑定  为机构新增机具
        $('#orgnaizationTbl').delegate('a[data-click="add"]', 'click', function() {
            var nId = $(this).data('clickData');
            _.route(_.resolvePath(sContextPath, 'devices', nId));
        });

		//查询机构        
        $('#searchBtn').click(function() {
            oDataTable.ajax.reload();
        });

    }

    // 画面初期化
    function onPageLoad() {

        // 画面初期事件绑定及JS插件渲染
        initializePage();
    }

    // ================================ 方法调用 ================================ //
    // 画面初期化
    onPageLoad();
});
