'use strict';
$(function() {

    // ================================ 变量声明 ================================ //
    // ContextPath
    var sContextPath = _.CONTEXT_PATH;
    // 机具信息一览表格
    var oDataTable;
    var nId = function() {
        if (location.pathname !== _.resolvePath(sContextPath, 'device')){
            var aslocatoin = location.pathname.split("/");
            // 获取 id
            return aslocatoin[aslocatoin.length - 1];
        }
    }();

    // ================================ 方法声明 ================================ //
    // JS插件渲染及画面初期事件绑定
    function initializePage() {
    	
    	if (nId != null) {
    		$('#deviceList').show();
    		 var oDataTableOption = _.DATATABLES_OPTIONS;
             oDataTableOption.ajax = {
                 url: _.resolvePath(sContextPath, 'api/devices', nId ), 
                 data: function(oData) {
                     $.extend(oData, $('form').serializeObject());
                 }
             };
             
             oDataTableOption.columns = [ 
                                         {
                                             width: '13%',
                                             data: 'tenpoNo'
                                         },
                                         {
                                             width: '13%',
                                             data: 'ipAddress'
                                         },            
                                         {
                                             width: '13%',
                                             data: 'connectPort'
                                         },            
                                         {
                                             width: '13%',
                                             data: 'kbsNo'
                                         },
                                         {
                                             width: '13%',
                                             data: 'secchiName'
                                         },
                                         {
                                             width: '13%',
                                             data: 'startDate'
                                         },
                                         {
                                             width: '13%',
                                             data: 'endDate'
                                         }
                                   ]
             
    		 oDataTableOption.columnDefs = [
    				{
    					createdCell : function(dTd, sData) {
    						$(dTd).attr('title', sData);
    					},
    					targets : [ 0, 1, 2, 3, 4, 5, 6 ]
    				},
    				{
    					createdCell : function(dTd, sData, oData) {
    						var kId = oData.kbsSeqNo;
    						var sBtn = '<a href="javascript:;"'
    								+ 'data-click="edit" data-click-data="'
    								+ kId
    								+ '" title="编辑"><i class="fa fa-pencil"></i></a>';

    						sBtn += '<a href="javascript:;" class="m-l"'
    								+ 'data-click="delete" data-click-data="'
    								+ kId
    								+ '" title="删除"><i class="fa fa-trash"></i></a>';

    						$(dTd).html(sBtn);
    					},
    					targets : [ 7 ]
    				} ]

    		// 获取初始化表格
    		oDataTable = $('#deviceTbl').DataTable(oDataTableOption);
    		
    		
    		//下级机构表格
             var oDataTableOption1 = _.DATATABLES_OPTIONS;
             oDataTableOption1.ajax = {
                 url: _.resolvePath(sContextPath, 'api/organizations/branch', nId),
                 data: function(oData) {
                     $.extend(oData, $('form').serializeObject());
                 }
             };
             
             oDataTableOption1.columns = [ 
                                         {
                                             width: '30%',
                                             data: 'tenpoNo'
                                         },
                                         {
                                             width: '30%',
                                             data: 'tenpoName'
                                         }
                                         /*,            
                                         {
                                             width: '25%',
                                             data: 'ipSeg'
                                         }
                                         ,            
                                         {
                                             width: '15%',
                                             data: 'startDate'
                                         }
                                         ,            
                                         {
                                             width: '15%',
                                             data: 'endDate'
                                         }*/
                                   ]
             
             oDataTableOption1.columnDefs = [
    				{
    					createdCell : function(dTd, sData) {
    						$(dTd).attr('title', sData);
    					},
    					targets : [ 0, 1]
    				},
    				{
    					createdCell : function(dTd, sData, oData) {
    						var nId = oData.tenpoSeqNo;
    						var sBtn = '<a href="javascript:;"'
    								+ 'data-click="next" data-click-data="'
    								+ nId
    								+ '" title="进入"><i class="fa fa-arrow-right"></i></a>';


    						$(dTd).html(sBtn);
    					},
    					targets : [ 2 ]
    				} ]

             
    		//下级机构表格初始化
             $('#orgnaizationTbl').DataTable(oDataTableOption1);

             		
             		
    		// 事件绑定
    		$('#deviceTbl').delegate('a[data-click="delete"]','click',
    			function() {
    				var kId = $(this).data('clickData');
    				$.alert(function(bConfirm) {
    					if (bConfirm) {
    						// 提交至后台删除数据
    						var sUrl = _.resolvePath(sContextPath,'api/device', kId);
    						$.del(sUrl).done(function() {
    							// 显示处理成功
    							$.confirm();
    							// 重新加载时不重置用户分页
    							oDataTable.ajax.reload(null, false);
    						});
    					}
    				});
    			}
    		);

    		//迁移编辑画面
    		$('#deviceTbl').delegate('a[data-click="edit"]', 'click',
    			function() {
    				var kId = $(this).data('clickData');
    				_.route(_.resolvePath(sContextPath, 'device', nId, kId));
    			}
    		);
    		
    		//迁移了级机构画面
    		$('#orgnaizationTbl').delegate('a[data-click="next"]', 'click',
    			function() {
    				var nId = $(this).data('clickData');
    				_.route(_.resolvePath(sContextPath, 'devices', nId));
    			}
    		);
    		
        	
    	}
    	
        //迁移新增机具画面
        $('#addBtn').click(function() {
            _.route(_.resolvePath(sContextPath, 'device',nId));
        });
        
        
        $('#backBtn').click(function() {
            back();
        });
    }

    // 跳回一览画面
    function back(){
        _.route(_.resolvePath(sContextPath, 'device'));
        
        var botenSeqNo = $('#botenSeqNo').val();
         if (botenSeqNo != 0) {
        	 _.route(_.resolvePath(sContextPath, 'devices',botenSeqNo));
         } else {
        	 _.route(_.resolvePath(sContextPath, 'device'));
        }
    }

    // 画面初期数据取得
    function getPageData() {
        if (nId != null) {
            var sUrl = _.resolvePath(sContextPath, 'api/device', nId);
            $.get(sUrl).done(function(oData) {
            	$('#tenpoSeqNo').val(oData.tenpoSeqNo)
                $('#tenpoNo').val(oData.tenpoNo);
                $('#tenpoName').val(oData.tenpoName);
                $('#botenSeqNo').val(oData.botenSeqNo);
            });
        }
    }

    // 画面初期化
    function onPageLoad() {

        // 画面初期数据取得
        getPageData();

        // 画面初期事件绑定及JS插件渲染
        initializePage();
    }

    // ================================ 方法调用 ================================ //
    // 画面初期化
    onPageLoad();
});
