'use strict';
(function () {

    if (_.isBackAvailable) {
        var elBack = document.querySelector('#goBack');
        elBack.style.display = 'initial';

        elBack.onclick = function() {
            window.history.back();
        };
    }

    document.querySelector('#goHome').onclick = function() {
        _.route(_.CONTEXT_PATH);
    };
})();