'use strict';
$(function() {

    // ================================ 变量声明 ================================ //
    // ContextPath
    var sContextPath = _.CONTEXT_PATH;
    // 公司信息一览表格
    var oDataTable;
    var nId = function() {
        if (location.pathname !== _.resolvePath(sContextPath, 'orgnaization')){
            var aslocatoin = location.pathname.split("/");
            // 获取 id
            return aslocatoin[aslocatoin.length - 1];
        }
    }();

    
    $("#tenpoNo").get(0).focus();
    // ================================ 方法声明 ================================ //
    // JS插件渲染及画面初期事件绑定
    function initializePage() {
    	
    	if (nId != null) {
    		$('#branchOrgList').show();
    		 var oDataTableOption = _.DATATABLES_OPTIONS;
             oDataTableOption.ajax = {
                 url: _.resolvePath(sContextPath, 'api/organizations/branch', nId),
                 data: function(oData) {
                     $.extend(oData, $('form').serializeObject());
                 }
             };
             
             oDataTableOption.columns = [ 
                                         {
                                             width: '30%',
                                             data: 'tenpoNo'
                                         },
                                         {
                                             width: '30%',
                                             data: 'tenpoName'
                                         }
                                         /*,            
                                         {
                                             width: '25%',
                                             data: 'ipSeg'
                                         }
                                         ,            
                                         {
                                             width: '15%',
                                             data: 'startDate'
                                         }
                                         ,            
                                         {
                                             width: '15%',
                                             data: 'endDate'
                                         }*/
                                   ]
             
    		 oDataTableOption.columnDefs = [
    				{
    					createdCell : function(dTd, sData) {
    						$(dTd).attr('title', sData);
    					},
    					targets : [ 0, 1 ]
    				},
    				{
    					createdCell : function(dTd, sData, oData) {
    						var nId = oData.tenpoSeqNo;
    						var sBtn = '<a href="javascript:;"'
    								+ 'data-click="edit" data-click-data="'
    								+ nId
    								+ '" title="编辑，新增下级机构"><i class="fa fa-pencil"></i></a>';

    						sBtn += '<a href="javascript:;" class="m-l"'
    								+ 'data-click="delete" data-click-data="'
    								+ nId
    								+ '" title="删除"><i class="fa fa-trash"></i></a>';

    						$(dTd).html(sBtn);
    					},
    					targets : [ 2 ]
    				} ]

    		// 获取初始化表格
    		oDataTable = $('#orgnaizationTbl').DataTable(oDataTableOption);

    		// 事件绑定
    		$('#orgnaizationTbl').delegate(
    				'a[data-click="delete"]',
    				'click',
    				function() {
    					var nId = $(this).data('clickData');
    					$.alert(function(bConfirm) {
    						if (bConfirm) {
    							// 提交至后台删除数据
    							var sUrl = _.resolvePath(sContextPath,
    									'api/orgnaization', nId);
    							$.del(sUrl).done(function() {
    								// 显示处理成功
    								$.confirm();
    								// 重新加载时不重置用户分页
    								oDataTable.ajax.reload(null, false);
    							});
    						}
    					});
    				});

    		$('#orgnaizationTbl').delegate('a[data-click="edit"]', 'click',
    				function() {
    					var nId = $(this).data('clickData');
    					_.route(_.resolvePath(sContextPath, 'orgnaization', nId));
    				});
        	
    	}
    	
    	
        // VALIDATION 验证
        var oValidationOption = _.VALIDATION_OPTIONS;
        oValidationOption.rules = {
        	tenpoNo: {
                required: true,
                number: true,
                fixedlength: 5
                
            },
            tenpoName: {
                required: true,
                chineseAlphabet: true
            }
            /*,
            ipSeg: {
            	required: true,
            	ipv4: true
            },
            ipMask: {
            	number: true,
                fixedlength: 5
            },
            startDate: {
            	required: true
            },
            endDate: {
            	required: true
            }*/

            
        };	

        $('#orgForm').validate(oValidationOption);        
        

        $('#saveBtn').click(function() {
            // 验证失败,则返回
            if (!$('#orgForm').valid()) {
                return false;
            }

            // 提交至后台,根据url进行区分
            var sUrl = _.resolvePath(sContextPath, 'api/orgnaization');
            var fMethod = $.post;
            if (nId != null) {
                sUrl = _.resolvePath(sContextPath, 'api/orgnaization', nId);
                fMethod = $.put;
            }
            fMethod(sUrl, $('#orgForm').serializeJSON()).done(function() {
                back();
            });
        })
        
         $('#createBtn').click(function() {
        	 _.route(_.resolvePath(sContextPath, 'orgnaization/branch', nId));
        })
        
        
        if (nId != null) {
        	$('#createBtn').show();
        }  else {
        	$('#createBtn').hide();
        }
        // datePicker
        $(".input-group.date").datepicker(_.DATEPICKER_OPTIONS);
        
        $('#backBtn').click(function() {
            back();
        });
    }

    // 跳回一览画面
    function back(){
    	 var botenSeqNo = $('#botenSeqNo').val();
         if (botenSeqNo != 0) {
        	 _.route(_.resolvePath(sContextPath, 'orgnaization',botenSeqNo));
         } else {
        	 _.route(_.resolvePath(sContextPath, 'orgnaizations'));
         }
       
    }

    // 画面初期数据取得
    function getPageData() {
        if (nId != null) {
            var sUrl = _.resolvePath(sContextPath, 'api/orgnaization', nId);
            $.get(sUrl).done(function(oData) {
                $('#tenpoNo').val(oData.tenpoNo);
                $('#tenpoName').val(oData.tenpoName);
                $('#ipSeg').val(oData.ipSeg);
                $('#ipMask').val(oData.ipMask);
                $('#startDate').val(oData.startDate);
                $('#endDate').val(oData.endDate);
                $('#botenSeqNo').val(oData.botenSeqNo);
                $('#updDate').val(oData.updDate);
//                $(".breadcrumb li:last-child strong").innerHTML  = "编辑";
            });
        }
    }

    // 画面初期化
    function onPageLoad() {

        // 画面初期事件绑定及JS插件渲染
        initializePage();

        // 画面初期数据取得
        getPageData();
    }

    // ================================ 方法调用 ================================ //
    // 画面初期化
    onPageLoad();
});
