'use strict';
$(function() {

    // ================================ 变量声明 ================================ //
    // ContextPath
    var sContextPath = _.CONTEXT_PATH;

    var nId = function() {
        if (location.pathname !== _.resolvePath(sContextPath, 'orgnaization/branch')){
            var aslocatoin = location.pathname.split("/");
            // 获取 id
            return aslocatoin[aslocatoin.length - 1];
        }
    }();

    $("#tenpoNo").get(0).focus();
    // ================================ 方法声明 ================================ //
    // JS插件渲染及画面初期事件绑定
    function initializePage() {
        // VALIDATION 验证
        var oValidationOption = _.VALIDATION_OPTIONS;
        oValidationOption.rules = {
        	tenpoNo: {
                required: true,
                number: true,
                fixedlength: 5
                
            },
        	tenpoName: {
                required: true,
                chineseAlphabet: true
            },
            /*ipSeg: {
            	ipv4: true
            },
            ipMask: {
            	number: true,
                fixedlength: 5
            },*/
            startDate: {
            	required: true
            },
            endDate: {
            	required: true
            }

            
        };	

        $('#orgForm').validate(oValidationOption);        
        

        $('#saveBtn').click(function() {
            // 验证失败,则返回
            if (!$('#orgForm').valid()) {
                return false;
            }

            // 提交至后台,根据url进行区分
            var sUrl = _.resolvePath(sContextPath, 'api/orgnaization/branch', nId);
            var fMethod = $.post;

            fMethod(sUrl, $('#orgForm').serializeJSON()).done(function() {
                back();
            });
        })


        //datePicker
        $(".input-group.date").datepicker(_.DATEPICKER_OPTIONS);
        
        $('#backBtn').click(function() {
            back();
        });
    }

    //跳回一览画面
    function back(){
        _.route(_.resolvePath(sContextPath, 'orgnaization', nId));
    }

    // 画面初期数据取得
    function getPageData() {
        if (nId != null) {
            var sUrl = _.resolvePath(sContextPath, 'api/orgnaization', nId);
            $.get(sUrl).done(function(oData) {
                $('#botenTenpoNo').val(oData.tenpoNo);
                $('#botenTenpoName').val(oData.tenpoName);
//                $(".breadcrumb li:last-child strong").innerHTML  = "编辑";
            });
        }
    }

    // 画面初期化
    function onPageLoad() {

        // 画面初期事件绑定及JS插件渲染
        initializePage();

        // 画面初期数据取得
        getPageData();
    }

    // ================================ 方法调用 ================================ //
    // 画面初期化
    onPageLoad();
});
