'use strict';
$(function() {

	$("#userIdView").get(0).focus();
    // ================================ 变量声明 ================================ //
    // ContextPath
    var sContextPath = _.CONTEXT_PATH;
    // 公司信息一览表格
    var oDataTable;

    var oId = function() {
        if (location.pathname !== _.resolvePath(sContextPath, 'user')){
            var aslocatoin = location.pathname.split("/");
            if (aslocatoin[aslocatoin.length - 1] == 'user') {
            	 return aslocatoin[aslocatoin.length - 2];
            }
            // 获取 id
            return aslocatoin[aslocatoin.length - 3];
        }
    }();
    var nId = function() {
        if (location.pathname !== _.resolvePath(sContextPath, 'user')){
            var aslocatoin = location.pathname.split("/");
            if (aslocatoin[aslocatoin.length - 1] == 'user') {
            	return null
            }
            // 获取 id
            return aslocatoin[aslocatoin.length - 1];
        }
    }();
    

    // ================================ 方法声明 ================================ //
    // JS插件渲染及画面初期事件绑定
    function initializePage() {
    	
    	if (nId != null) {
    		$('#userIdView').attr("disabled", true);
    	}
    	
        // VALIDATION 验证
        var oValidationOption = _.VALIDATION_OPTIONS;
        oValidationOption.rules = {
        	userId: {
                required: true,
                number: true,
                maxlength: 11
            },
        	userIdView: {
                required: true,
                number: true,
                maxlength: 11
            },
            userName: {
                chineseAlphabet: true
            },
            startDate: {
            	required: true
            },
            endDate: {
            	required: true
            }
            
        };	

        $('#userForm').validate(oValidationOption);        
        

        $('#saveBtn').click(function() {
            // 验证失败,则返回
            if (!$('#userForm').valid()) {
                return false;
            }

            // 提交至后台,根据url进行区分
            var sUrl = _.resolvePath(sContextPath, 'api/user', oId);
            var fMethod = $.post;
            $('#userId').val( $('#userIdView').val());
            if (nId != null) {
                sUrl = _.resolvePath(sContextPath, 'api/user', nId);
                fMethod = $.put;
            }
            fMethod(sUrl, $('#userForm').serializeJSON()).done(function() {
                back();
            });
        })

        // datePicker
        $(".input-group.date").datepicker(_.DATEPICKER_OPTIONS);
        
        $('#backBtn').click(function() {
            back();
        });
    }

    // 跳回一览画面
    function back(){
        _.route(_.resolvePath(sContextPath, 'orgnaization', oId, 'users'));
    }

    // 画面初期数据取得
    function getPageData() {
        if (nId != null) {
            var sUrl = _.resolvePath(sContextPath, 'api/user', nId);
            $.get(sUrl).done(function(oData) {
                $('#userIdView').val(oData.userId);
                $('#userId').val(oData.userId);
                $('#userName').val(oData.userName);
                $('#startDate').val(oData.startDate);
                $('#endDate').val(oData.endDate);
                $('#updDate').val(oData.updDate);
                $('#roleId').val(oData.roleId);
                $('#userType').val(oData.userType);
            });
        }
    }

    // 画面初期化
    function onPageLoad() {

        // 画面初期事件绑定及JS插件渲染
        initializePage();

        // 画面初期数据取得
        getPageData();
    }

    // ================================ 方法调用 ================================ //
    // 画面初期化
    onPageLoad();
});
