'use strict';
$(function() {

    // ================================ 变量声明 ================================ //
    // ContextPath
    var sContextPath = _.CONTEXT_PATH;
    // 公司信息一览表格
    var oDataTable;
    var nId = function() {
        if (location.pathname !== _.resolvePath(sContextPath, 'keys')){
            var aslocatoin = location.pathname.split("/");
            // 获取 id
            return aslocatoin[aslocatoin.length - 2];
        }
    }();

    // ================================ 方法声明 ================================ //
    // JS插件渲染及画面初期事件绑定
    function initializePage() {
    	
    	if (nId != null) {
    		 var oDataTableOption = _.DATATABLES_OPTIONS;
             oDataTableOption.ajax = {
                 url: _.resolvePath(sContextPath, 'api/device', nId, 'keys'),
                 data: function(oData) {
                     $.extend(oData, $('form').serializeObject());
                 }
             };
             
             oDataTableOption.columns = [ 
                                         {
                                             width: '15%',
                                             data: 'holderNo'
                                         },
                                         {
                                             width: '20%',
                                             data: 'keyName'
                                         },                       
                                         /*{
                                             width: '15%',
                                             data: 'keysyuName'
                                         },*/                       
                                         {
                                             width: '20%',
                                             data: 'startTime'
                                         },            
                                         {
                                             width: '20%',
                                             data: 'endTime'
                                         },
                                         {
                                             width: '20%',
                                             data: 'timeOut'
                                         }
                                   ]
             
    		 oDataTableOption.columnDefs = [
    				{
    					createdCell : function(dTd, sData) {
    						$(dTd).attr('title', sData);
    					},
    					targets : [ 0, 1, 2, 3, 4 ]
    				},
    				{
    					createdCell : function(dTd, sData, oData) {
    						var kId = oData.keySeqNo;
    						var sBtn = '<a href="javascript:;"'
    								+ 'data-click="edit" data-click-data="'
    								+ kId
    								+ '" title="编辑"><i class="fa fa-pencil"></i></a>';

    						$(dTd).html(sBtn);
    					},
    					targets : [ 5 ]
    				} ]

    		// 获取初始化表格
    		oDataTable = $('#keyTbl').DataTable(oDataTableOption);          
             
    		

    		$('#keyTbl').delegate('a[data-click="edit"]', 'click',
    				function() {
    					var nKeyId = $(this).data('clickData');
    					_.route(_.resolvePath(sContextPath,  'key', nKeyId));
    				});    		
        	
    	    }    
        

        
        
        
        $('#backBtn').click(function() {
            back();
        });
    }

    // 跳回一览画面
    function back(){
        _.route(_.resolvePath(sContextPath, 'devices'));      
       
    }

    // 画面初期数据取得
    function getPageData() {
        if (nId != null) {
            var sUrl = _.resolvePath(sContextPath, 'api/keys/device', nId);
            $.get(sUrl).done(function(oData) {
                $('#tenpoName').val(oData.tenpoName);
                $('#ipAddress').val(oData.ipAddress);
                $('#connectPort').val(oData.connectPort);
            });
        }
    }

    // 画面初期化
    function onPageLoad() {

        // 画面初期事件绑定及JS插件渲染
        initializePage();

        // 画面初期数据取得
        getPageData();
    }

    // ================================ 方法调用 ================================ //
    // 画面初期化
    onPageLoad();
});
