'use strict';
$(function() {

    // ================================ 变量声明 ================================ //
    // ContextPath
    var sContextPath = _.CONTEXT_PATH;
    // 公司信息一览表格
    var oDataTable;
    var nId = function() {
        if (location.pathname !== _.resolvePath(sContextPath, 'keytype')){
            var aslocatoin = location.pathname.split("/");
            // 获取 id
            return aslocatoin[aslocatoin.length - 1];
        }
    }();

    $("#keysyuName").get(0).focus();
    // ================================ 方法声明 ================================ //
    // JS插件渲染及画面初期事件绑定
    function initializePage() {
    	
        	
    	
        // VALIDATION 验证
        var oValidationOption = _.VALIDATION_OPTIONS;
        oValidationOption.rules = {
        	keysyuName: {
                required: true,
                maxlength: 40,
                chineseAlphabet: true
            }
            
        };	

        $('#keyTypeForm').validate(oValidationOption);        
        

        $('#saveBtn').click(function() {
            // 验证失败,则返回
            if (!$('#keyTypeForm').valid()) {
                return false;
            }

            // 提交至后台,根据url进行区分
            var sUrl = _.resolvePath(sContextPath, 'api/keytype');
            var fMethod = $.post;
            if (nId != null) {
                sUrl = _.resolvePath(sContextPath, 'api/keytype', nId);
                fMethod = $.put;
            }
            fMethod(sUrl, $('#keyTypeForm').serializeJSON()).done(function() {
                back();
            });
        })
        
        
        
        $('#backBtn').click(function() {
            back();
        });

        
	} 
        
    // 跳回一览画面
    function back(){
        _.route(_.resolvePath(sContextPath, 'keytypes'));
    }

    // 画面初期数据取得
    function getPageData() {
        if (nId != null) {
            var sUrl = _.resolvePath(sContextPath, 'api/keytype', nId);
            $.get(sUrl).done(function(oData) {
                $('#keysyuName').val(oData.keysyuName);
                $('#updDate').val(oData.updDate);
            });
        }
    }

    // 画面初期化
    function onPageLoad() {

        // 画面初期事件绑定及JS插件渲染
        initializePage();

        // 画面初期数据取得
        getPageData();
    }

    // ================================ 方法调用 ================================ //
    // 画面初期化
    onPageLoad();
});
